import React, {Component} from 'react';
import {Row, Col,Container} from 'react-bootstrap';
import {Hasil, ListCategories, NavbarComponent,Menus} from './components';
import {API_URL} from './utils/contants'
import axios from 'axios'
import swal from 'sweetalert'

export default class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
       menus : [],
       keranjangs : [],
    }
  }

  componentDidMount(){
    axios
    .get(API_URL+"barangs")
    .then(res => {
      // console.log(res)
      const menus = res.data;
      this.setState({ menus });
    })
    .catch(error =>{
    console.log(error)
    })
  }

  masukKeranjang = (value) => {
    axios
    .post(API_URL+"keranjangs")
    .then(res => {
      // console.log(res)
      swal({
        title: "Good job!",
        text: "You clicked the button!",
        icon: "success",
        button: false,
      });
    })
    .catch(error =>{
    console.log(error)
    })
  }

  render() {
    const {menus} = this.state
    return (
      <div className="App">
      <NavbarComponent/>
      <div className="mt-4">
         <Container fluid>
             <Row>
               <ListCategories/>
                   <Col>
                       <h4><strong>Barang</strong></h4>
                       <hr/>
                          <Row>
                            {menus && menus.map((menu) => (
                             <Menus
                              key={menu.id}
                              menu = {menu}
                              masukKeranjang={this.masukKeranjang}
                             />
                            ))}
                          </Row>
                   </Col>
               <Hasil/>
         </Row>
         </Container>
      </div>
     </div>
    )
  }
}
