import React, { Component} from 'react'
import {Col, ListGroup, Row, Badge, Modal, Button} from 'react-bootstrap' 
import { numberWithCommas } from '../utils/utils'
import TotalBayar from './TotalBayar'

export default class Hasil extends Component {
    constructor(props){
        super(props)
        this.state = {
            showModal :false,
            keranjangDetail : false,
            jumlah:0,
        }
    }

    handleShow = (menuKeranjang) => {
        this.setState({
            showModal: true,
            keranjangDetail:  menuKeranjang,
        })
    }

    handleClose = () => {
        this.setState({
            showModal: false,
        })
    }
    render() {
        const {keranjangs} = this.props
        return (
            <Col md={3} mt={2}>
                <h4><strong>Keranjang</strong></h4>
                <hr/>
                    {keranjangs.length !== 0 && (
                        <ListGroup variant="flush">
                            {keranjangs.map((menuKeranjang) =>
                                <ListGroup.Item key={menuKeranjang.id} onClick={() => this.handleShow(menuKeranjang)}>
                                    <Row>
                                        <Col xs={2}>
                                        <Badge variant="primary">{menuKeranjang.jumlah}</Badge>
                                        </Col>
                                        <Col  xs={6}>
                                            <p className="mb-0"><small>{menuKeranjang.product.nama_barang}</small></p>
                                            <p className="mb-0"><small><b>Rp. {numberWithCommas(menuKeranjang.product.harga)}</b></small></p>
                                        </Col>
                                        <Col>
                                            <p className="float-right"><small><b>{numberWithCommas(menuKeranjang.total_harga)}</b></small></p>
                                        </Col>
                                    </Row>
                                </ListGroup.Item>
                            )}
                    <Modal show={this.state.showModal} onHide={this.handleClose}>
                            <Modal.Header closeButton>
                            <Modal.Title>Modal heading</Modal.Title>
                            </Modal.Header>
                            <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                            <Modal.Footer>
                            <Button variant="secondary" onClick={this.handleClose}>
                                Close
                            </Button>
                            <Button variant="primary" onClick={this.handleClose}>
                                Save Changes
                            </Button>
                            </Modal.Footer>
                        </Modal>
                        </ListGroup>
                    )}
                    <TotalBayar keranjangs={keranjangs} {...this.props}/>
            </Col>
        )
    }
}
